import { Component, Input, OnInit } from '@angular/core';
import { trigger, state, style, animate, transition } from '@angular/animations';

@Component({
  selector: 'app-photo-carousel',
  templateUrl: './photo-carousel.component.html',
  styleUrls: ['./photo-carousel.component.css'],
  animations: [
    trigger('slideAnimation', [
      state('0', style({ transform: 'translateX(0)' })),
      state('1', style({ transform: 'translateX(-100%)' })),
      state('-1', style({ transform: 'translateX(100%)' })),
      transition('* => *', animate('500ms ease')),
    ])
  ]
})
export class PhotoCarouselComponent implements OnInit {
  @Input() images: string[] = [];
  currentIndex = 0;

  ngOnInit() {
    setInterval(() => this.slideToNext(), 5000); // Adjust the slide duration as needed (e.g., 5000ms = 5 seconds)
  }

  slideToNext() {
    this.currentIndex = (this.currentIndex + 1) % this.images.length;
  }

  slideToPrev() {
    this.currentIndex = (this.currentIndex - 1 + this.images.length) % this.images.length;
  }
}
